import { Component, Prop, Vue } from "vue-property-decorator";
import Swal, { SweetAlertType } from "sweetalert2";
import VueSimpleAlert from "vue-simple-alert";
import { VMoney } from "v-money";

import { Category } from "@/models/category";
import INotification from "@/models/INotification";
import { Product, IProduct, IProductVariant, ImageURl } from "@/models/product";

import productsService from "@/services/products.service";

import ProductImageCarousel from "./product-image-carousel/ProductImageCarousel.vue";
import ProductLoadingScreen from "./product-loading-screen/ProductLoadingScreen.vue";

@Component({
  directives: { money: VMoney },
  components: { ProductImageCarousel, ProductLoadingScreen },
})
export class ProductModal extends Vue {
  @Prop() propProductSelected!: Product;
  @Prop() propCategories!: Category[];
  @Prop() propCategorySelected!: Category;
  @Prop() propCreateProduct!: boolean;
  @Prop() propEditProduct!: boolean;
  @Prop() propIsService!: boolean;

  public productSelected?: Product = this.propProductSelected;
  public categorySelected?: Category = this.propCategorySelected;
  public categories: Category[] = this.propCategories;
  public showCreateProduct: boolean = this.propCreateProduct;
  public showEditProduct: boolean = this.propEditProduct;
  public showCreateService: boolean = this.propIsService;

  public showDeleteProduct = false;
  public showPublishedProduct = false;
  public showEditImage = false;
  public showImportImage = false;
  public showCloseModal = false;

  public canReorderImages = true;

  public isPublished = this.productSelected
    ? !this.productSelected.is_published
    : false;

  public title: string = "";
  public subtitle: string = "";
  public titleBtn: string = "";

  public categoryGuid = "";

  public responseError = {
    name: "",
    isName: false,
    description: "",
    ordering: "",
    category: "",
    variantValue: [] as string[],
  };

  public fieldErrors = {
    name: "",
    description: "",
  };

  public image: any;
  public selectedImage: string = "";
  public imageToCrop: any;
  public cropper: any;
  public destination: string;
  public price: any;
  public showCropper: boolean;
  public newCategory: any;
  public loggedUser: any;
  public notify?: INotification | string = ``;
  public multipleImages: string[] = [];
  public hasEditImage: boolean = false;
  public positionSelected: number = 0;
  public editImage: boolean = false;
  public priceToMatch: boolean = false;
  public hasCategorySelected: boolean = true;

  public inputLengthCounter = 512;
  public logoInput = "";
  public confirmeDeleteModalController = false;
  public productId = "";

  public loadingController = false;
  public creatingProduct = false;

  public finalImage: string = "";
  public textAreaModel = "";
  public isSale = false;

  public hasReordering: boolean = false;
  public hasAddImage: boolean = false;
  public hasRemoval: boolean = false;

  public indexImages: number[] = [];
  public indexImagesRemove: number[] = [];

  public currentImages: ImageURl[] = [];
  public previousImages: ImageURl[] = [];

  public money = {
    decimal: ",",
    thousands: ".",
    prefix: "R$ ",
    suffix: "",
    precision: 2,
    masked: false,
  };

  private productService = new productsService();

  constructor() {
    super();
    this.showCropper = false;
    this.destination = "";
    this.imageToCrop = "";
  }

  created() {
    const logged: any = localStorage.getItem("loggedUser");
    this.loggedUser = JSON.parse(logged);
  }

  mounted() {
    this.setInfoForm();
    this.multipleImages = ["1", "2", "3", "4"];
    this.setImage();
    this.selectCategoryType();
  }

  setImage() {
    if (this.productSelected && this.productSelected.image_list?.length) {
      this.selectedImage = this.productSelected.image_list[0].image_url
        ? this.productSelected.image_list[0].image_url
        : "";
    }
  }

  setInfoForm() {
    if (this.showCreateProduct && !this.showCreateService) {
      this.setTitles(
        "Cadastrar Produto",
        "Insira informaes e cadastre seus produtos",
        "Salvar Produto"
      );
      this.validateInfosCategory();
    } else if (this.showCreateProduct && this.showCreateService) {
      this.setTitles(
        "Cadastrar Servio",
        "Insira informaes e cadastre seus servios",
        "Salvar Servio"
      );
      this.validateInfosCategory();
    } else {
      if (!this.showCreateService) {
        this.setTitles(
          "Editar Produto",
          "Edite as informaes do seu produto",
          "Atualizar produto"
        );
      } else if (this.showCreateService) {
        this.setTitles(
          "Editar Servio",
          "Edite as informaes do seu servio",
          "Atualizar servio"
        );
      }
      this.validateInfosCategory();
    }
  }

  private setTitles(title: string, subtitle: string, titleBtn: string) {
    this.title = title;
    this.subtitle = subtitle;
    this.titleBtn = titleBtn;
  }

  public selectedCategory() {
    this.categorySelected = this.categories.find(
      (category) => category.guid === this.categoryGuid
    );
    if (this.categorySelected?.variant_status) {
      this.createProductSkus(this.categorySelected);
    }
  }

  public selectCategoryType() {
    if (this.showCreateService) {
      this.categories = this.categories.filter(
        (value) => value.register_type === "Service"
      );
    } else {
      this.categories = this.categories.filter(
        (value) => value.register_type === "Product"
      );
    }
  }

  private createProductSkus(categorySelected?: Category) {
    this.productSelected?.skus.splice(0, this.productSelected?.skus.length);
    categorySelected && categorySelected.variant_value.length
      ? categorySelected.variant_value.map((values) =>
          this.productSelected?.skus.push(this.resetSkus(values.value))
        )
      : this.productSelected?.skus.push(this.resetSkus());
  }

  private resetSkus(value: string = "") {
    return {
      code: "",
      guid: "",
      new_price: "",
      old_price: "",
      vtex_id: 0,
      value,
      in_promotion: false,
      is_skus: false,
      status: true,
    };
  }

  validateInfosCategory() {
    if (this.categorySelected) {
      this.categoryGuid = this.categorySelected.guid;
    }
  }

  private setProduct(): IProduct {
    return {
      category_guid: this.categoryGuid,
      name: this.productSelected ? this.productSelected.name : "",
      description: this.productSelected
        ? this.productSelected?.description
        : "",
      ordering: this.showCreateProduct ? 100 : this.productSelected?.ordering,
      is_published: this.showCreateProduct
        ? true
        : this.productSelected?.is_published,
      variant_value: this.setVariants(),
      register_type: this.setRegisterType(),
      price_to_match: this.priceToMatch
        ? true
        : this.productSelected?.price_to_match,
    };
  }

  private setVariants(): IProductVariant[] {
    const variants: IProductVariant[] = [];
    this.responseError.variantValue = [];
    this.productSelected?.skus.forEach((values) => {
      values.status && values.old_price === "R$ 0,00"
        ? this.responseError.variantValue.push(
            "A variante no tem preo e est ativada"
          )
        : this.responseError.variantValue.push("");

      this.showCreateProduct
        ? variants.push({
            code: values.code ? values.code : "",
            value: values.value ? values.value : "",
            new_price: values.new_price
              ? parseFloat(this.validateCurrency(values.new_price.toString()))
              : parseFloat(this.validateCurrency(values.old_price.toString())),
            old_price: parseFloat(
              this.validateCurrency(values.old_price.toString())
            ),
            status: values.status ? values.status : false,
          })
        : variants.push({
            guid: values.guid ? values.guid : "",
            code: values.code ? values.code : "",
            value: values.value ? values.value : "",
            new_price:
              values.in_promotion && values.new_price
                ? parseFloat(this.validateCurrency(values.new_price.toString()))
                : parseFloat(
                    this.validateCurrency(values.old_price.toString())
                  ),
            old_price: parseFloat(
              this.validateCurrency(values.old_price.toString())
            ),
            status: values.status ? values.status : false,
          });
    });
    return variants;
  }

  private setRegisterType() {
    if (this.showCreateService) {
      return "Service";
    } else {
      return "Product";
    }
  }

  private validateProductFields() {
    const errors: {
      name: string;
      description: string;
      category: string;
    } = {
      name: "",
      description: "",
      category: "",
    };
    let inputsAreValid = true;
    if (!this.productSelected!.name) {
      errors.name = "O seu PRODUTO precisa ter um nome";
      inputsAreValid = false;
    }
    if (!this.productSelected!.description) {
      errors.description = "O seu PRODUTO precisa ter uma descrio";
      inputsAreValid = false;
    }
    if (!this.hasCategorySelected!) {
      errors.category =
        "Voc precisa selecionar uma categoria para o seu PRODUTO";
      inputsAreValid = false;
    }
    this.setResponseError(errors);
    return inputsAreValid;
  }
  public toMatch(productSelected) {
    this.priceToMatch = productSelected;
    if (this.priceToMatch) {
      this.productSelected!.skus[0].old_price = "R$ 0,01";
      this.productSelected!.skus[0].new_price = "R$ 0,01";
    } else {
      this.productSelected!.skus[0].old_price = "R$ 0,00";
      this.productSelected!.skus[0].new_price = "R$ 0,00";
    }
  }

  public saveProduct() {
    this.hasCategory();
    if (this.showCreateProduct && !this.validateProductFields()) {
      return;
    }
    if (this.productSelected && this.categoryGuid) {
      this.showCreateProduct
        ? this.createProduct(this.setProduct())
        : this.editProduct(this.setProduct(), this.productSelected.guid);
    }
  }

  isEmpty = (array: string[]) => array.filter((item) => item !== "").length;

  delay = (ms) => new Promise((res) => setTimeout(res, ms));

  private async createProduct(product: IProduct) {
    if (this.isEmpty(this.responseError.variantValue) < 1) {
      this.loadingController = true;
      this.creatingProduct = true;

      const response = await this.productService.createProduct(
        product,
        this.categoryGuid
      );

      await this.delay(500);

      this.creatingProduct = false;

      await this.delay(500);

      if (response.status === 202) {
        const data = await response.json();
        product.variant_value.forEach(
          (variant, index) => (variant.guid = data.skus[index])
        );
        await this.editProduct(product, data.guid);
      } else {
        await this.checkStatusOfProduct(product, response);
      }

      this.closeProductModal();
      if (product.register_type === "Service") {
        window.location.reload();
        this.toastMessage(
          "success",
          `Pronto, seu Servio j foi ${
            this.showCreateProduct ? "criado" : "atualizado"
          } com sucesso.`
        );
      } else {
        window.location.reload();
        this.toastMessage(
          "success",
          `Pronto, seu Produto j foi ${
            this.showCreateProduct ? "criado" : "atualizado"
          } com sucesso.`
        );
      }
      this.loadingController = false;
    }
  }

  private hasCategory() {
    if (this.categoryGuid) {
      this.hasCategorySelected = true;
      return;
    }
    this.hasCategorySelected = false;
  }

  private async editProduct(product: IProduct, guid: string) {
    this.loadingController = true;
    const response = await this.productService.editProduct(product, guid);
    await this.checkStatusOfProduct(product, response, guid);
    this.closeProductModal();
    this.loadingController = false;
  }

  onUploadProgress(event, position) {
    let progress: number = Math.round((event.loaded * 100) / event.total);

    if (this.productSelected && this.productSelected.image_list) {
      this.productSelected.image_list[position - 1].uploadProgress = progress;
    }
  }

  private async updateImage(productGuid: string, productSelected: Product) {
    if (productSelected && productSelected.image_list) {
      await Promise.all(
        productSelected.image_list.map((image) =>
          this.productService.uploadImage(
            {
              position: image.position,
              // description: image.description,
              data: image.imageFile,
              onUploadProgressCallback: this.onUploadProgress,
            },
            productGuid
          )
        )
      )
        .then(async () => {
          await this.getCategories();
        })
        .catch(() => {
          this.nextStatusError(
            `Houve um erro ao carregar os produtos. Favor tentar novamente.`
          );
        });
    }
  }

  private async uploadImage(
    productGuid: string,
    indexImages: number[],
    productSelected: Product
  ) {
    await Promise.all(
      indexImages.map((value) => {
        if (productSelected && productSelected.image_list) {
          return this.productService
            .uploadImage(
              {
                position: productSelected.image_list[value].position,
                data: productSelected.image_list[value].imageFile,
                onUploadProgressCallback: this.onUploadProgress,
              },
              productGuid
            )
            .then(async (response) => {
              switch (response.status) {
                case 400:
                  await this.updateImage(productGuid, productSelected);
                  break;
              }
            });
        }
      })
    );
    indexImages = [];
  }

  private async checkStatusOfProduct(
    product: IProduct,
    response: Response,
    guid: string = ""
  ) {
    switch (response.status) {
      case 201:
        const guidProduct = await response.json();
        if (this.hasAddImage) {
          await this.uploadImage(
            guidProduct,
            this.indexImages,
            this.productSelected as Product
          );
        }
        break;
      case 202:
        if (
          this.hasAddImage ||
          this.hasRemoval ||
          this.hasReordering ||
          this.editImage
        ) {
          if (this.hasAddImage) {
            await this.uploadImage(
              guid,
              this.indexImages,
              this.productSelected as Product
            );
          }
          if (this.editImage) {
            await this.updateImage(guid, this.productSelected as Product);
          }
          if (this.hasRemoval) {
            this.removeImage(guid);
          }
        }
        break;
      case 304:
        this.nextStatusError(`O produto ${product.name} j existe`);
        this.setResponseError({
          name: ["Ops, verifique se j no existe um Produto com esse Nome."],
        });
        this.responseError.isName = true;
        break;

      case 400:
        this.setResponseError(await response.json());
        this.nextStatusError(
          `Verifique se todos os campos foram preenchidos corretamente`
        );
        this.loading(false);
        break;

      case 404:
        this.setResponseError({ category: "Selecione uma Categoria." });
        break;

      case 405:
      case 423:
        this.toastMessage("error", "Seu login expirou, entre novamente.");
        localStorage.removeItem("pertinho");
        localStorage.removeItem("loggedUser");
        this.$router.push("login");
        break;

      default:
        this.loading(false);
        break;
    }
  }

  private setResponseError(data) {
    this.responseError.category = data.category;
    this.responseError.name =
      data.name && Array.isArray(data.name) ? data.name[0] : data.name;
    this.responseError.description =
      data.description && Array.isArray(data.description)
        ? data.description[0]
        : data.description;
    this.responseError.ordering =
      data.ordering && data.ordering.length ? data.ordering[0] : data.ordering;
  }

  private nextStatusSuccess(msg: string) {
    this.loading(false);
    this.toastMessage("success", msg);
    this.getCategories();
    this.closeProductModal();
  }

  private nextStatusError(msg: string) {
    this.loading(false);
    this.toastMessage("error", msg);
  }

  private nextStatusDefault(msg: string) {
    this.loading(false);
    this.toastMessage("error", msg);
  }

  public loading($event) {
    this.loadingController = false;
    this.$emit("loading", $event);
  }

  public getCategories() {
    this.$emit("getCategories", true);
  }
  public closeProductModal() {
    this.$emit("closeProductModal", true);
    if (this.hasRemoval || this.editImage) {
      this.getCategories();
    }
  }

  public openCloseModal() {
    this.showCloseModal = true;
  }

  public closeCloseModal() {
    this.showCloseModal = false;
  }

  public openModalDelete(product: Product) {
    this.productSelected = product;
    this.showDeleteProduct = true;
  }

  public closeDeleteProductModal() {
    this.showDeleteProduct = false;
  }

  public openPublishedModal() {
    this.showPublishedProduct = true;
  }

  public closePublishedModal() {
    this.showPublishedProduct = false;
  }

  public cancelPublishedModal() {
    this.isPublished = !this.isPublished;
    this.closePublishedModal();
  }

  public publishedProduct() {
    if (this.productSelected) {
      this.productSelected.is_published = !this.productSelected.is_published;
      this.isPublished = !this.productSelected.is_published;
      this.closePublishedModal();
    }
  }

  private validateCurrency(x: string) {
    let money: string = "";
    if (x) {
      money = x.replace("R$ ", "");
      money = money.replace(",", "");
      money = money.replace(".", "");
    }
    return money;
  }

  public toastMessage(type: SweetAlertType, title: string) {
    VueSimpleAlert.fire({
      type,
      title,
      toast: true,
      position: "top-end",
      showConfirmButton: false,
      timer: 6000,
      onOpen: (toast) => {
        toast.addEventListener("mouseenter", Swal.stopTimer),
          toast.addEventListener("mouseleave", Swal.resumeTimer);
      },
    });
  }

  public destroySelected() {
    this.$emit("destroyProductSelected", true);
    this.$emit("destroyCategorySelected", true);
  }

  async updatePositionImage(productGuid: string) {
    this.currentImages.forEach(async (image, index) => {
      if (image.position !== this.previousImages[index].position) {
        const response = await this.productService.updatePositionImage(
          { position: image.position },
          productGuid,
          this.previousImages[index].position
        );
        switch (response.status) {
          case 200:
          case 201:
          case 202:
            this.nextStatusSuccess(
              `Pronto, a posio da imagem do Produto foi atualizada com sucesso.`
            );
            break;
        }
      }
    });
  }

  async removeImage(productGuid: string) {
    this.indexImagesRemove.forEach(async (position) => {
      const response = await this.productService.deleteImage(
        productGuid,
        position
      );
      switch (response.status) {
        case 202:
          this.nextStatusSuccess(
            `Pronto, imagem do Produto removida com sucesso.`
          );
          break;
      }
    });
    this.indexImagesRemove = [];
  }

  public openInput(position: number, edit: boolean) {
    this.positionSelected = position;
    this.editImage = edit;
    this.showImportImage = true;
  }

  public closeImportImage(imageUrl) {
    if (imageUrl) {
      this.canReorderImages = false;
      if (!this.editImage) {
        this.hasAddImage = true;
      }
      if (this.productSelected && this.productSelected.image_list) {
        this.indexImages.push(imageUrl.position - 1);
        this.$set(
          this.productSelected.image_list,
          imageUrl.position - 1,
          imageUrl
        );
        // this.productSelected.image_list[imageUrl.position - 1] = imageUrl;
        this.setImage();
      }
    }
    this.showImportImage = false;
  }

  public removeImages(position: number) {
    this.hasRemoval = true;
    this.indexImagesRemove.push(position);

    this.canReorderImages = false;

    if (this.productSelected && this.productSelected.image_list) {
      this.productSelected.image_list.splice(position - 1, 1);
    }
  }

  public previousReorderImages(imagesList: ImageURl[]) {
    this.previousImages = imagesList;
  }
  public currentReorderImage(imagesList: ImageURl[]) {
    this.currentImages = imagesList;
    if (this.productSelected && this.productSelected.image_list) {
      this.productSelected.image_list = this.currentImages;
    }
  }

  public setViewImage(image: string) {
    this.selectedImage = image;
  }

  public openEditImage() {
    this.showEditImage = true;
  }

  public closeEditImageModal() {
    this.showEditImage = false;
  }

  public saveEditImage() {
    this.hasEditImage = false;
  }

  public isMobile = () =>
    Boolean(
      /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
        navigator.userAgent
      )
    );
}

export { ProductModal as default };
