import { Component, Prop, Vue } from 'vue-property-decorator';
import INotification from '@/models/INotification';
import productsService from '@/services/products.service';
import userService from '@/services/user.service';
import shippingService from '@/services/shipping.service';

import getUtm from '@/helpers/getUtm';

declare global {
    interface Window {
        opera: any;
    }
}

@Component({})
export class Negocio extends Vue {

    @Prop({ default: '<strong>Minha loja online!</strong>' }) titulo!: string;

    private productService = new productsService();
    private userService = new userService();
    private shippingService = new shippingService();

    public logo: boolean = false;
    public shipping: any = '';
    public facebook: string = '';
    public instagram: string = '';
    public website: string = '';
    public modalLinkCompartilhe: boolean = false;
    public modalCompartilhe: boolean = false;
    public showModalQrCode: boolean = false;
    public showModalFullQrCode: boolean = false;
    public scorePerfil: any = '';
    public scorePerfilSeller: any = '';
    public percentual: any = '';
    public scorePercentual: number = 0;
    public showScore: boolean = false;

    public qrCodeUrl: string = '';
    public lex: boolean = false;
    public slugUrl?: string;
    public notify?: INotification | string = ``;
    public content: any = '';
    public address: any;
    public payments: any[] = [];
    public paymentsArray: any[] = [];
    public checkedNames: any[] = [];
    public checked: boolean = false;
    public items: object[] = [];
    public categoryImg: string = '';
    public hasPaymentMethod: boolean = false;
    public hasProducts: boolean = false;
    public hasShipping: boolean = false;
    public loggedUser: any;
    public openingHours: any[] = [
        { week_day: 1, label: 'Segunda', hours: false },
        { week_day: 2, label: 'Terça', hours: false },
        { week_day: 3, label: 'Quarta', hours: false },
        { week_day: 4, label: 'Quinta', hours: false },
        { week_day: 5, label: 'Sexta', hours: false },
        { week_day: 6, label: 'Sabado', hours: false },
        { week_day: 7, label: 'Domingo', hours: false },
    ];
    public scoreMissings: any[] = [];
    public scoreRedirect: string = '';
    public scoreRedirectInvite: string = '/minha-loja'

    get isApp() {
        return this.$store.state.environment.isApp;
      }


    constructor() {
        super();

        this.modalLinkCompartilhe = false;
        this.modalCompartilhe = false;

        this.userService.listObterPagamentos()
            .then(data => {
                data.forEach(choice => {
                    this.payments.push(choice);
                });
            });

        this.userService.listProdcutsAll().then(data => {
            this.setField(data);
            this.frete();
            if (!data.mobile_confirmed) {
                this.lex = !this.lex;
                this.notificar(data);
            } else if (!data.has_payment_method) {
                this.infoAlertPayments(data);
            } else if (!data.has_shipping) {
                this.infoAlertFrete(data);
            } else if (!data.has_products) {
                this.infoAlertProducts(data);
            } else {
                this.infoAlertBlog(data);
            }
        });


        this.userService.getHorario()
            .then(data => data.json())
            .then(data => {
                this.openingHours.forEach(hour => {
                    data.forEach(element => {
                        if (element.week_day === hour.week_day) {
                            hour.hours = `${element.opening} às ${element.closing}`;
                        }
                    });
                    this.openingHours.forEach(dataHour => {
                        if (!dataHour.hours) {
                            dataHour.hours = 'Fechado!';
                        }
                    });
                });
                this.openingHours = data;
            });

        this.userService.getSocialMedia()
            .then(data => {
                data.forEach(social => {
                    this.setSocialMedia(social);
                });
            });

    }

    mounted() {
        this.generateQrCode();
        this.score();
    }

    async score() {
        const score = await this.userService.scoreSeller();
        const scoreFull = 100;
        if (score.sum_score >= 8) {
            this.percentual = `width: 100%;`;
        }
        else {
            this.percentual = `width: ${score.percentual}%;`;
        }

        this.scorePercentual = score.percentual;
        this.scorePerfil = score.status_score;
        this.scorePerfilSeller = score.status_score;

        if (score !== 'seller not have score') {
            this.showScore = true;
        }

        if (this.scorePerfilSeller === 'Completo' && score.percentual < scoreFull) {
            this.scorePerfilSeller = 'Completo';
        }
        const stage = {
            'Iniciante': {
                'logo': { value: '- Adicione uma logo a sua loja.', path: '/minha-loja' },
                'description': { value: '- Adicione uma descrição com mais de 200 caracteres.', path: '/minha-loja' },
                'payment_methods': { value: '- Adicione meios de pagamento.', path: '/minha-loja' }
            },
            'Médio': {
                'social_networks': { value: '- Cadastre suas redes sociais.', path: '/minha-loja' },
                'shipping': { value: '- Adicione 3 opções de frete e entrega para obter a nota máxima neste critério.', path: '/minha-loja' },
                'shipping_alert': { value: '- Os clientes adoram frete grátis, se possível, cadastre essa opção para aumentar sua nota.', path: '/minha-loja' },
                'products_quantity': { value: '- Cadastre ao menos 10 produtos.', path: '/produtos' },
                'open_hours': { value: '- Cadastre seus horários de funcionamento e totalize 40 horas semanais para obter a nota máxima neste critério.', path: '/minha-loja' },
                'categories_quantity': { value: '- Cadastre ao menos 3 categorias de produtos.', path: '/produtos' }
            },
            'Completo': {
                'promotion': { value: '- Cadastre promoções em seus produtos.', path: '/produtos' },
                'products_photos': { value: '- Cadastre fotos em todos os produtos.', path: '/produtos' },
                'minimum_order': { value: '- Cadastre um valor mínimo de pedido que seja inferior ao valor médio dos produtos da sua vitrine.', path: '/minha-loja' },
                'orders_and_contacts': { value: '- Receba contatos e pedidos', path: '/produtos' },
                'payment_methods': { value: '- Adicione pelo menos 8 meios de pagamento.', path: '/minha-loja' },
                'full_profile': { value: 'Parabéns por completar seu perfil! Siga essas dicas para melhorar ainda mais sua vitrine e potencializar suas vendas.' }
            }
        }

        if (score.sum_score >= 8) {
            if (!this.scoreMissings.includes('- Parabéns por completar seu perfil! Siga essas dicas para melhorar ainda mais sua vitrine e potencializar suas vendas.')) {
                this.scoreMissings.push(stage['Completo']['full_profile']);
            }
        }

        for (const title in stage[this.scorePerfil]) {
            if (score[title] === 0) {
                this.scoreMissings.push(stage[this.scorePerfil][title]);
            }
        }

        if (this.scorePerfil === 'Medio') {
            for (const title in stage['Iniciante']) {
                if (score[title] === 0) { this.scoreMissings.push(stage['Iniciante'][title]) }
            }
        }

        if (this.scorePerfil === 'Completo') {
            for (const title in stage['Iniciante']) {
                if (score[title] === 0) { this.scoreMissings.push(stage['Iniciante'][title]) }
            }
            for (const title in stage['Medio']) {
                if (score[title] === 0) { this.scoreMissings.push(stage['Medio'][title]) }
            }
        }



        if (score.description < 2) {
            if (!this.scoreMissings.includes('- Adicione uma descrição com mais de 200 caracteres.')) {
                this.scoreMissings.push(stage['Iniciante']['description']);
            }
        }

        if (score.shipping < 4 && score.shipping !== 3 && score.shipping !== 0 && (this.scorePerfil === 'Médio' || this.scorePerfil === 'Completo')) {
            if (!this.scoreMissings.includes('- Adicione 3 opções de frete e entrega para obter a nota máxima neste critério.')) {
                this.scoreMissings.push(stage['Médio']['shipping']);
            }
        }

        if (score.shipping === 3 && (this.scorePerfil === 'Médio' || this.scorePerfil === 'Completo')) {
            if (!this.scoreMissings.includes('- Os clientes adoram frete grátis, se possível, cadastre essa opção para aumentar sua nota.')) {
                this.scoreMissings.push(stage['Médio']['shipping_alert']);
            }
        }

        if (score.products_quantity < 3 && (this.scorePerfil === 'Médio' || this.scorePerfil === 'Completo')) {

            this.scoreMissings.forEach(e => {
                if (e.value != "- Cadastre ao menos 10 produtos.") {
                    this.scoreMissings.push(stage['Médio']['products_quantity']);
                }


            })
        }

        if (score.open_hours < 3 && score.open_hours !== 0 && (this.scorePerfil === 'Médio' || this.scorePerfil === 'Completo')) {
            if (!this.scoreMissings.includes('- Cadastre seus horários de funcionamento e totalize 40 horas semanais para obter a nota máxima neste critério.')) {
                this.scoreMissings.push(stage['Médio']['open_hours']);
            }
        }

        if (score.categories_quantity < 2 && (this.scorePerfil === 'Médio' || this.scorePerfil === 'Completo')) {
            if (!this.scoreMissings.includes('- Cadastre ao menos 3 categorias de produtos.')) {
                this.scoreMissings.push(stage['Médio']['categories_quantity']);
            }
        }

        if (score.promotion < 2 && this.scorePerfil === 'Completo') {
            if (!this.scoreMissings.includes('- Cadastre promoções em seus produtos.')) {
                this.scoreMissings.push(stage['Completo']['promotion']);
            }
        }

        if (score.products_photos < 9 && this.scorePerfil === 'Completo') {
            if (!this.scoreMissings.includes('- Cadastre fotos em todos os produtos.')) {
                this.scoreMissings.push(stage['Completo']['products_photos']);
            }
        }

        if (score.minimum_order < 3 && this.scorePerfil === 'Completo') {
            if (!this.scoreMissings.includes('- Cadastre um valor mínimo de pedido que seja inferior ao valor médio dos produtos da sua vitrine.')) {
                this.scoreMissings.push(stage['Completo']['minimum_order']);
            }
        }

        if (score.orders_and_contacts < 2 && this.scorePerfil === 'Completo') {
            if (!this.scoreMissings.includes('- Receba contatos e pedidos')) {
                this.scoreMissings.push(stage['Completo']['orders_and_contacts']);
            }
        }

        if (score.payment_methods < 2 && this.scorePerfil === 'Completo') {
            if (!this.scoreMissings.includes('- Adicione pelo menos 8 meios de pagamento.')) {
                this.scoreMissings.push(stage['Completo']['payment_methods']);
            }
        }

        this.scoreRedirect = this.scoreMissings.length ? this.scoreMissings[0].path : '';
    }

    frete() {
        const logged: any = localStorage.getItem('loggedUser');
        this.loggedUser = JSON.parse(logged);
        this.shippingService.getShippingDetail(this.loggedUser.slug)
            .then(data => {
                if (data.ok) {
                    data.json().then(shipping => {
                        this.shipping = shipping;
                        if (shipping.estimated_time === '01:00:00') {
                            this.shipping.estimated_time = 'Em até 1h';
                        }
                        if (shipping.estimated_time === '23:59:59') {
                            this.shipping.estimated_time = 'Em até 1d';
                        }
                        if (shipping.estimated_time === '00:00:00') {
                            this.shipping.estimated_time = 'a combinar';
                        }
                    });
                }
            });
    }

    goToProducts() {
        this.$router.push('produtos');
    }

    public openModalQrCode() {
        this.showModalQrCode = true;
    }

    public closeModalQrCode() {
        this.showModalQrCode = false;
    }

    public openModalFullQrCode() {
        this.showModalQrCode = false;
        this.showModalFullQrCode = true;
    }

    public closeModalFullQrCode() {
        this.showModalFullQrCode = false;
        this.showModalQrCode = true;
    }

    async generateQrCode() {
        const pertinho = JSON.parse(localStorage.getItem('pertinho') || '');
        const response = await this.userService.qrcodeCatalog(pertinho.guid);
        const {message}  = await response.json();
        this.qrCodeUrl = message;
    }

    setField(texts: any) {
        if (texts.store_logo_url) {
            this.logo = true;
            this.categoryImg = texts.store_logo_url;
        } else {
            this.categoryImg = texts.category_image_url;
        }
        this.content = texts;
        this.slugUrl = `https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}`;
        this.content.about = texts.about;

        (texts.hide_address)
            ? this.content.address = texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state
            : this.content.address = texts.address.street + ', ' + texts.address.number + ', ' + texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state;

        this.content.mobile = this.content.mobile.replace(/(\d{2})(\d{5})(\d{4})/, (regex, arg1, arg2, arg3) => `(${arg1}) ${arg2} - ${arg3}`);
    }

    notificar(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
                <strong>Você tem pendências!</strong><br>
                ${data.mobile_confirmed
                    ? ''
                    : `Você não ativou a sua conta via WhatsApp.
                    Por favor, verifique se você não recebeu o link de ativação no seu WhatsApp.
                    Se não recebeu, por favor clique no botão abaixo para reenviar a mensagem`
                }
        `,
            color: '#ffd1c4',
            buttons: true,
            buttonText: 'REENVIAR MENSAGEM',
            activeButtons: true,
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }

    infoAlertBlog(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
            <strong>A Pertinho agora tem mais uma novidade!</strong>
            <br/>
            Que tal trocarmos experiências com outros empreendedores na comunidade de empreendedores do Facebook?
            <br/>
            Faça parte agora mesmo <a class="text-black" style="text-decoration:underline;" href="https://www.facebook.com/groups/1020347875087965/" target="_blank">clicando aqui.</a>
        `,
            color: '#c3e4e7',
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }

    infoAlertAvailableConta(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
        Falta pouco.....
        `,
            color: '#c3e4e7',
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }

    infoAlertPayments(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><br>Mostre aos seus clientes quais meios de pagamento você aceita.<br >Cadastre agora mesmo <strong><a href="edit#meiosdepagamentos" style="color:#525252">clicando aqui</a></strong>.
        `,
            color: '#c3e4e7',
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }

    infoAlertFrete(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><br>Mostre aos seus clientes quais são suas condições de frete. <Br>Cadastre agora mesmo <strong><a href="edit#frete" style="color:#525252">clicando aqui</a></strong>.
        `,
            color: '#c3e4e7',
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }

    infoAlertProducts(data) {
        this.$emit('notification', {
            icon: require('./../../../assets/images/icon_error.png'),
            text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><Br>Cadastre seus produtos e comece a vender através da sua lojinha. <br >Cadastre agora mesmo <strong><a href="produtos" style="color:#525252">clicando aqui</a></strong>.
        `,
            color: '#c3e4e7',
            active: true,
            type: 'error',
            text_align_left: true,
        });
    }


    compLink() {
        (document.getElementById('myTextNegocio') as HTMLInputElement).value = `https://www.pertinhodecasa.com.br/vitrine/${this.loggedUser.slug}?${getUtm('link', false)}`;
    }

    copiarLink() {
        const copyText = document.getElementById('myTextNegocio');
        (copyText as HTMLInputElement).select();
        (copyText as HTMLInputElement).setSelectionRange(0, 99999);
        document.execCommand('copy');
        alert('Link copiado!');
    }

    complinkLoja() {
        window.open(`https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('link', false)}`);
    }

    compWhatsapp() {
        window.open(`https://api.whatsapp.com/send?text=Ei,%20pessoal!%20Conheçam%20a%20minha%20loja%20dentro%20da%20Pertinho%20de%20Casa!%20Compre%20no%20site%20ou%20pelo%20app!%0Ahttps://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('whatsapp')}`);
    }

    compFacebook() {
        window.open(`https://www.facebook.com/sharer/sharer.php?u=http%3A%2F%2Fwww.pertinhodecasa.com.br%2Fvitrine%2F${this.content.slug}?${getUtm('facebook')}&src=sdkpreparse`);
    }

    compMessenger() {
        window.open(`https://www.facebook.com/login.php?u=http%3A%2F%2Fwww.pertinhodecasa.com.br%2Fvitrine%2F${this.content.slug}?${getUtm('messenger')}&src=sdkpreparse`);
    }

    compTwitter() {
        window.open(`https://twitter.com/intent/tweet?url=https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('twitter')}&text=Ei,%20pessoal!%20Conheçam%20a%20minha%20loja%20dentro%20da%20Pertinho%20de%20Casa!%20Compre%20no%20site%20ou%20pelo%20app!`);
    }
    setSocialMedia(media) {
        if (media.type === 'Facebook') {
            this.facebook = Array.from(new Set(media.profileUrl.split('https://www.facebook.com/'))).toString();
            this.facebook = this.facebook.replace(/,/g, '');
        }
        if (media.type === 'Instagram') {
            this.instagram = Array.from(new Set(media.profileUrl.split('https://www.instagram.com/'))).toString();
            this.instagram = this.instagram.replace(/,/g, '');
        }
        if (media.type === 'Personal Website') {
            this.website = Array.from(new Set(media.profileUrl.split('https://www'))).toString();
            this.website = this.website.replace(/,./g, '');
        }
    }
    linkSocialMedia(media) {
        if (media === 'facebook') {
            window.open(`https://www.facebook.com/${this.facebook}`);
        }
        if (media === 'instagram') {
            window.open(`https://www.instagram.com/${this.instagram}`);
        }
        if (media === 'website') {
            window.open(`https://www.${this.website}`);
        }
    }
}
