import { Component, Vue }       from 'vue-property-decorator';
import draggable                from 'vuedraggable';
import { VMoney }               from 'v-money';
import VueSimpleAlert           from 'vue-simple-alert';
import Swal, { SweetAlertType } from 'sweetalert2';

import categoryService          from '@/services/category.service';
import productsService          from '@/services/products.service';
import { Category }             from '@/models/category';
import {Product }               from '@/models/product';
import Seller                   from '@/models/seller';
import { accordion }            from '@/components/accordion/Accordion.vue';

@Component({
    components: {
        draggable,
    },
    directives: { money: VMoney },
})
export class ProdutosView extends Vue {

    public loadingController: boolean = false;
    public drag: boolean = false;

    public showImportProduto: boolean = false;
    public showCreateCategory: boolean = false;
    public showEditCategory: boolean = false;
    public showCreateProduct: boolean = false;
    public showEditProduct: boolean = false;
    public showDeleteProduct: boolean = false;
    public showRegisterOptions: boolean = false;
    public isRegisterService: boolean = false;
    public isCategoryRegisterService: boolean = false;
    public showImportProducts: boolean = false;

    public help: string = '';
    public openAllCategories = false;
    public categories: Category[] = [];
    public categoryName: string = '';
    public categorySelected: Category = {
        name: '',
        guid: '',
        deleted: false,
        description: '',
        ordering: 0,
        products: [],
        vtext_id: 0,
        vtext_full_path: false,
        variant_name: '',
        variant_status: false,
        variant_value: [
            { value: '', guid: '' },
            { value: '', guid: '' },
        ],
        register_type: '',
    };

    public productSelected: Product = {
        guid: '',
        name: '',
        description: '',
        ordering: 100,
        is_published: true,
        image_list: [],
        skus: [{
            code: '',
            guid: '',
            new_price: '',
            old_price: '',
            vtex_id: 0,
            value: '',
            in_promotion: false,
            is_skus: false,
            status: true,
        }],
        register_type: '',
        price_to_match: false,
    };

    public money = {
        decimal: ',',
        thousands: '.',
        prefix: 'R$ ',
        precision: 2,
        masked: false,
    };

    private loggedUser: Seller = { slug: '' };
    public totalProducts: number = 0;

    private categoryService = new categoryService();
    private productService = new productsService();

    constructor() {
        super();
    }

    mounted() {
        const logged: any = localStorage.getItem('loggedUser');
        this.loggedUser = JSON.parse(logged);
        this.getCategories();
    }

    public async getCategories() {
        this.loadingController = true;
        const response = await this.categoryService.getCategories(this.loggedUser.slug);
        const categories: Category[] = await response.json();
        this.totalProducts = 0;
        if (categories) {
            categories.forEach((category: Category) => this.getProducts(category));
            this.categories = this.sortCategories(categories);
        } else {
            this.toastMessage('error', 'Erro ao carregar a lista de categoria.');
        }
        this.loadingController = false;
    }

    private async getProducts(category: Category) {
        const response = await this.productService.getProducts(category.guid);
        const products: Product[] = await response.json();
        this.totalProducts = this.totalProducts + products.length;

        if (products) {
            this.setProductsInCategories(products, category);
            this.setPercent(products);
        } else {
            this.toastMessage('error', 'Erro ao carregar a lista de produtos.');
        }
    }

    private setPercent(products: Product[]) {
        return products.map((product: Product, index: number) => {
            if (product.skus[index]) {
                // if (product.skus[index].in_promotion) {
                //     Vue.set(product.skus[index], 'percent', this.percentValue(product.skus[index].old_price.toString(), product.skus[index].new_price.toString()) + '%');
                // }
                (product.skus[index].code !== '')
                    ? Vue.set(product.skus[index], 'is_skus', true)
                    : Vue.set(product.skus[index], 'is_skus', false);
            }
        });
    }

    private setProductsInCategories(products: Product[], category: Category) {
        (products.length)
            ? Vue.set(category, 'products', products)
            : Vue.set(category, 'products', false);
    }

    private sortCategories = (categories: Category[]) => categories.sort((a, b) => a.ordering - b.ordering);

    public getDragOptions(group: string) {
        return {
            animation: 200,
            group,
            disabled: false,
            ghostClass: 'ghost',
        };
    }

    public reorderCategories(categories: Category[]) {
        categories.forEach((category, index) => this.categoryService.updateCategory({ name: category.name, ordering: index + 1, register_type: category.register_type }, category.guid));
    }

    public reoderProducts(categoryName: string) {
        const catReorderProds = this.categories.filter(x => x.name === categoryName);
        if (catReorderProds && catReorderProds[0].products) {
            catReorderProds[0].products.forEach((product: Product, index) => this.productService.editProduct({ ordering: index + 1 }, product.guid));
        }
    }

    public openImportProducts() {
        this.showImportProducts = true;
        this.showRegisterOptions = false;
    }

    public closeImportProductsModal() {
        this.showImportProducts = false;
    }

    public openAddCategoryModal() {
        this.showCreateCategory = true;
        this.showRegisterOptions = false;
    }

    public openAddCategoryServiceModal() {
        this.isCategoryRegisterService = true;
        this.showCreateCategory = true;
        this.showRegisterOptions = false;
    }

    public openEditCategoryModal(categorySelected: Category) {
        if(categorySelected.register_type === 'Service'){
            this.isCategoryRegisterService = true;
        } else{
            this.isCategoryRegisterService = false;
        }
        this.categorySelected = this.checkCategory(categorySelected);
        this.showEditCategory = true;
    }

    public closeCategoryModal() {
        this.showCreateCategory = false;
        this.showEditCategory = false;
        this.isCategoryRegisterService = false;

        this.destroyCategorySelected();
    }


    private checkCategory(category: Category) {
        return {
            name: category.name,
            guid: category.guid,
            deleted: category.deleted,
            description: category.description,
            ordering: category.ordering,
            products: category.products,
            vtext_id: category.vtext_id,
            vtext_full_path: category.vtext_full_path,
            variant_name: category.variant_name ? category.variant_name : '',
            variant_status: category.variant_status ? category.variant_status : false,
            variant_value: category.variant_value.length ? category.variant_value : [{ value: '', guid: '' }, { value: '', guid: '' }],
            register_type: category.register_type,
        };
    }

    private destroyCategorySelected() {
        this.categorySelected = {
            name: '',
            guid: '',
            deleted: false,
            description: '',
            ordering: 0,
            products: [],
            vtext_id: 0,
            vtext_full_path: false,
            variant_name: '',
            variant_status: false,
            variant_value: [
                { value: '', guid: '' },
                { value: '', guid: '' },
            ],
            register_type: '',
        };
    }

    public openAddProductModal(category: Category) {
        this.showRegisterOptions = false;
        this.$router.push(`/produtos/criar/produto/${category.guid}`);
    }

    public openAddServiceModal(category: Category) {
        this.showRegisterOptions = false;
        this.$router.push(`/produtos/criar/servico/${category.guid}`);
    }

    public async openEditProductModal(category: Category, product: Product) {
        await this.$router.push(`/produtos/editar/${product.guid}`);
    }

    public closeProductModal() {
        this.showCreateProduct = false;
        this.showEditProduct = false;

        this.destroyProductSelected();
        this.destroyCategorySelected();
    }

    async uploadImage(productGuid: string, indexImages: number[], productSelected: Product) {
        // await Promise.all(
        //     indexImages.map(value => {
        //     if (productSelected && productSelected.image_list) {
        //         return this.productService.uploadImage({
        //             position: productSelected.image_list[value].position,
        //             data: productSelected.image_list[value].image_url,
        //             onUploadProgressCallback: () => {},
        //         }, productGuid).then(async response => {
        //             switch (response.status) {
        //                 case 400:
        //                     await this.updateImage(productGuid, productSelected);
        //                     break;
        //             }
        //         });
        //     }
        // }));
        // indexImages = [];
    }

    async updateImage(productGuid: string, productSelected: Product) {
        // if (productSelected && productSelected.image_list) {
        //     await Promise.all(productSelected.image_list.map(image =>
        //         this.productService.updateImage({
        //             position: image.position,
        //             data: image.image_url,
        //             onUploadProgressCallback
        //         }, productGuid),
        //     )).then(async () => {
        //         await this.getCategories();
        //     }).catch(() => {
        //         this.nextStatusError(`Houve um erro ao carregar os produtos. Favor tentar novamente.`);
        //     });
        // }
    }

    private async nextStatusSuccess(msg: string) {
        this.toastMessage('success', msg);
        this.getCategories();
    }

    private async nextStatusError(msg: string) {
        this.toastMessage('error', msg);
        this.getCategories();
    }

    private createProductSkus(categorySelected: Category) {
        if (categorySelected.variant_status && categorySelected.variant_value.length) {
            this.productSelected.skus.splice(0, 1);
            categorySelected.variant_value.forEach(variantValue => {
                this.productSelected.skus.push({
                        code: '',
                        guid: '',
                        new_price: '',
                        old_price: '',
                        vtex_id: 0,
                        value: variantValue.value,
                        in_promotion: false,
                        is_skus: false,
                        status: true,
                    });
            });
        }
    }

    private destroyProductSelected() {
        this.productSelected = {
            guid: '',
            name: '',
            description: '',
            ordering: 100,
            is_published: true,
            image_list: [],
            skus: [{
                code: '',
                guid: '',
                new_price: '',
                old_price: '',
                vtex_id: 0,
                value: '',
                in_promotion: false,
                is_skus: false,
                status: true,
            }],
            register_type: '',
            price_to_match: false,
        };
    }

    public openDeleteProductModal(productSelected: Product) {
        this.productSelected = productSelected;
        this.showDeleteProduct = true;
    }

    public closeDeleteProductModal() {
        this.showDeleteProduct = false;
    }

    public openImportProductModal() {
        this.showImportProduto = true;
    }

    public closeImportProductModal() {
        this.showImportProduto = false;
    }

    public loading($event: boolean) {
        this.loadingController = $event;
    }

    private toastMessage(type: SweetAlertType , title: string) {
        VueSimpleAlert.fire({
            type,
            title,
            toast: true,
            position: 'top-end',
            showConfirmButton: false,
            timer: 6000,
            onOpen: (toast) => {
                toast.addEventListener('mouseenter', Swal.stopTimer),
                toast.addEventListener('mouseleave', Swal.resumeTimer);
            },
        });
    }

    public percentValue = (oldPrice: string, newPrice: string) => (parseFloat(this.validateCurrency(newPrice.toString())) * 100 / parseFloat(this.validateCurrency(oldPrice.toString())) - 100).toFixed(0);

    private validateCurrency = (value: string) => value.replace('R$ ', '').replace(',', '').replace(',', '');

    public helpMsg(msg: string, status?: boolean) {
        switch (msg) {
            case 'edit-prod':
                this.help = `
                    <p>Clique para <span class="text-primary">EDITAR</span> o produto:</p>
                    <ul class="mt-1">
                        <li class="my-0 list-style-type-circle"><small>Categoria do produto</small></li>
                        <li class="my-0 list-style-type-circle"><small>Ordem do produto</small></li>
                        <li class="my-0 list-style-type-circle"><small>Imagem do produto</small></li>
                        <li class="my-0 list-style-type-circle"><small>Nome do produto</small></li>
                        <li class="my-0 list-style-type-circle"><small>Descrio do produto</small></li>
                        <li class="my-0 list-style-type-circle"><small>Valor do produto</small></li>
                        <hr>
                        <li class="my-0 list-style-type-circle"><small>Adicionar promoo</small></li>
                        <li class="my-0 list-style-type-circle"><small>Pausar anncio</small></li>
                        <li class="my-0 list-style-type-circle"><small>Excluir produto</small></li>
                    </ul>
                    `;
                break;

            case 'move-prod':
                this.help = 'Clique e arraste para <span class="text-info">MOVER</span> o Produto, isso afetar a posio do Produto dentro da Categoria na vitrine da loja.';
                break;

            case 'status-prod':
                status
                    ? this.help = `Produto com status <span class="help-active">ATIVO</span>, portanto ele aparecer na vitrine da loja. Clique para editar.`
                    : this.help = `Produto com status <span class="help-desactive">PAUSADO</span>, portanto ele no aparecer na vitrine da loja. Clique para editar.`;
                break;

            case 'add-prod-cad':
                this.help = 'Clique para <span class="text-success">ADICIONAR</span> novos Produtos nesta Categoria.';
                break;

            case 'add-prod':
                this.help = 'Clique para <span class="text-success">ADICIONAR</span> novos Produtos.';
                break;

            case 'add-cat':
                this.help = 'Clique para <span class="text-success">ADICIONAR</span> novas Categoria.';
                break;

            case 'move-cat':
                this.help = 'Clique e arraste para <span class="text-info">MOVER</span> a Categoria, isso afetar a posio da Categoria na vitrine da loja.';
                break;

            case 'edit-cat':
                this.help = 'Clique para <span class="text-primary">EDITAR</span> o nome da Categoria.';
                break;

            case 'add-svg':
                this.help = 'Clique para <span class="text-warning">IMPORTAR</span> Categorias e Produtos diretamente de um arquivo CSV.';
                break;

            case 'delete-prod':
                this.help = 'Clique para <span class="text-danger">EXCLUIR</span> o Produto.';
                break;

            default:
                this.help = '';
                break;
        }
    }

    public changeAccordionCategoriesAll() {
        this.openAllCategories = !this.openAllCategories;
        const accordions = this.$refs.accordionCategory as accordion;
        this.categories.map((category, index) => accordions[index].toggleAccordion());
    }

    public changeAccordionCategories(index: number) {
        const accordions = this.$refs.accordionCategory as accordion;
        accordions[index].toggleAccordion();
    }

    public registerOptions(){
        if(this.showRegisterOptions){
            this.showRegisterOptions = false;
        } else{
            this.showRegisterOptions = true;
        }
    }
}
