            headers: this.headersToken,
        };
        const response = await fetch(`${environment.setAPI()}/seller/${UserStorage.returnGuid()}/sign_logo/`, requestOptions);
        const { code: { url, fields } } = await response.json();

        return await this.uploadToS3({
            url,
            fields,
            image: img,
            onUploadProgressCb: cb,
        });
    }

