import { Component, Prop, Vue, Watch } from 'vue-property-decorator';
import VueSlider from 'vue-slider-component';
import VueSimpleAlert from 'vue-simple-alert';
import Swal, { SweetAlertType } from 'sweetalert2';
import 'vue-slider-component/theme/antd.css';
import Cropper from 'cropperjs';

import INotification from '@/models/INotification';
import focusInput from '@/helpers/focusInput';
import userService from '@/services/user.service';
import categoryService from '@/services/category.service';
import registerService from '@/services/register.service';
import environment from "@/environments/environment";

import Compressor from 'compressorjs';

import LogoModal from '@/components/logo-modal/logo-modal.vue'

declare var google: any;

@Component({
  components: {
    VueSlider,
    LogoModal
  },
})

export class editarMeuNegocio extends Vue {

  private userService = new userService();
  private categoryService = new categoryService();
  private registerService = new registerService();

  public showAddressChecked: boolean = true;
  public showAddress: boolean = true;
  public showCropper2: boolean = false;
  public validSubCategory: boolean = false;
  public categorySubcategory: any[] = [];
  public catSubcat: string = '';
  public autoCompleteSubCategoryOpen: boolean = false;
  public subcategory: string = '';
  public subcategories: any[] = [];
  public subcategoriesData: any[] = [];
  public comboCategories: any[] = [];
  public usersname: Array<{
    name: string,
    value: string,
    img: string,
    selected: boolean,
  }> = [];
  public instaImg: string = '';
  public userI: string = '';
  public userName: any;
  public validInsta: boolean = false;
  public autoCompleteInstaOpen: boolean = false;
  public user: any = '';
  public users: any[] = [];
  public face: any;
  public instg: any;
  public teste: any;
  public teste2: boolean = false;
  public facebookUser: boolean | string = false;
  public instagramUser: boolean | string = false;
  public finalImage: string = '';
  public showCropper: boolean = false;
  public destination: string = '';
  public logoInput: string = '';
  public publishAvailable: boolean = false;
  public publishAvailableText: string = '';
  public publishAvailableColor: string = '';
  public image: HTMLImageElement = new Image();
  public cropper: any;
  public imageToCrop: any;
  public map: any;
  public circles: any[] = [];
  public lat: any;
  public lng: any;
  public processStyle = {
    color: '#00b0d0',
    height: 7,
  };
  public rangeValue: number = 1;
  public voltar: boolean = true;
  public content: any | undefined = '';
  public locationSelected;
  public contentPayments: any = '';
  public payments: any[] = [];
  public moneys?: any[];
  public paymentsChecked: string = '';
  public facebook: string = '';
  public instagram: string = '';
  public website: string = '';
  public selectedlang: string = '';
  public googleApiReturn: any;
  public name: string = '';
  public email: string = '';
  public celular: string = '';
  public category: string = '';
  public about: string = '';
  public address: string = '';
  public complemento: string = '';
  public descricao: string = '';
  public numero: string = '';
  public cep: string = '';
  public logradouro: string = '';
  public bairro: string = '';
  public cidade: string = '';
  public documentCpf: string = '';
  public region: string = '';
  public size: string = '';
  public sellerName: string = '';

  public regions?: any[] = [];
  public deliveryRegions: any[] = [];
  public socialMedia: any[] = [];
  public profileUrl: any[] = [];
  public location: any;
  public categoryId: any;
  public errorMsg: boolean = false;
  public autoCompleteAddress: any;
  public autoCompleteRegion: any;
  public markers: [] = [];
  public places: [] = [];
  public currentPlace: string = '';
  public formModel: any = {};
  public checkedNames: object[] = [];

  @Prop() isActive: any = false;
  @Prop({ default: undefined }) inputList!: object[];
  @Prop({ default: '' }) form!: string;
  @Prop({ default: '<strong>Loja online</strong>' }) titulo!: string;
  @Prop({ default: 'Aqui voc pode confirmar seus dados e editar as informaes sobre o seu negcio' }) description!: string;

  @Prop({ default: '' }) propActiveAccordion!: string;
  @Prop({ default: true }) propHeader!: boolean;

  @Prop({ default: undefined }) notify?: INotification | string = ``;
  @Watch('notify') onPropertyChanged(value: string, oldValue: string) {
    // console.log(value, oldValue);
  }

  public activeAccordion = this.propActiveAccordion;
  public hasHeader = this.propHeader;

  public checked: boolean = false;
  public items: any[] = [];
  public focused = new focusInput();
  public reg: RegExp = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,24}))$/;
  public valid: boolean = false;
  public validAddress: boolean = false;
  public validComplemento: boolean = false;
  public validDescricao: boolean = false;
  public validCategory: boolean = false;
  public validNumero: boolean = false;
  public validAbout: boolean = false;
  public validRegion: boolean = false;
  public validLogradouro: boolean = false;
  public validCidade: boolean = false;
  public validBairro: boolean = false;
  public validCep: boolean = false;

  public categories: Array<{
    id: string,
    value: string,
    selected: boolean,
    name: string,
    subcategories: any[]
  }> = [];
  public autoCompleteCategoryOpen: boolean = false;
  public loggedUser: any;
  public hideAddress: boolean = false;
  public sizes: Array<{
    id: string,
    value: string,
    sigla: string,
    selected: boolean,
  }> = [];
  public showLogoModal = false;

  public genericChatbot: string = 'minhaloja_0';
  public chatbotId: string = this.genericChatbot;

  public setChatbotId(chatbotId, isActive) {
    this.chatbotId = isActive ? `minhaloja_${chatbotId}` : this.genericChatbot;
  }

  public selectAllPayments: boolean = false;
  @Watch('payments', { deep: true })
  onPaymentsChanged(val: boolean) {
    this.selectAllPayments = this.payments.map(payment => {
      return payment.methods.map(method => method.active);
    }).flat().filter(value => !value).length === 0;
  }

  async created() {
    this.showCropper2 = false;
    this.destination = '';
    this.showCropper = false;
    this.imageToCrop = '';
    this.usersname = this.getAllUsers();
    this.sizes = [];
    this.processChoicesSellerSize();

    const categoriesResponse = await this.registerService.getListCategories();
    const categoriesData = await categoriesResponse.json();

    this.categories = categoriesData || [];

    this.userService.listProdcutsAll().then(data => {
      data.delivery.forEach(region => this.setRegions(region));

      if (!data.document) {
        this.infoAlertNoDocument();
        document.getElementById('document')?.removeAttribute('disabled');
      } else if (!data.category) {
        this.infoAlertNoCategory();
      } else if (!data.address) {
        this.infoAlertNoAdress();
      }
      this.setField(data);
      this.celularValidate();
      this.cpfValidate();
    });
    // tslint:disable-next-line:no-unused-expression
    this.printValues;

    this.userService.listPagamentos()
      .then(async (data) => {
        const formatedPayments = [];
        // tslint:disable-next-line:forin
        for (const key in data) {
          await data[key].forEach(method => {
            method.active = this.checked;
          });
          this.payments.push({ title: key, methods: data[key] });
        }

        /**
         * Service listObterPagamentos
         * @param listObterPagamentos
         * servico que lista os meios de pagamentos alterados
         * na funcao alterarPayments, trazendo selecionados
         */
        this.userService.listObterPagamentos()
          // tslint:disable-next-line:no-shadowed-variable
          .then(data => {
            data.forEach(choice => {
              this.payments.forEach(paymentCategory => {
                paymentCategory.methods.map(x => x.active = x.name === choice.name ? true : x.active);
              });
            });
          });
      });
    this.userService.getSocialMedia()
      .then(data => {
        data.forEach(social => {
          this.setSocialMedia(social);
        });
      });
  }

  onAccordionToggle() {

  }

  /**
   * Function verify event checkbox
   * @param printValues
   * @param name
   * @param status
   */
  printValues(name, status) {
    if (status) {
      this.checkedNames.push(name);
    } else {
      this.checkedNames = this.checkedNames.filter((item) => item !== name);
    }
  }

  setSocialMedia(media) {
    if (media.type === 'Facebook') {
      this.facebook = Array.from(new Set(media.profileUrl.split('https://www.facebook.com/'))).toString();
      this.facebook = this.facebook.replace(/,/g, '');
    }
    if (media.type === 'Instagram') {
      this.instagram = Array.from(new Set(media.profileUrl.split('https://www.instagram.com/'))).toString();
      this.instagram = this.instagram.replace(/,/g, '');
    }
    if (media.type === 'Personal Website') {
      this.website = media.profileUrl.split('https://')[1];
    }
    this.face = this.facebook;
    this.instg = this.instagram;
  }


  setField(texts: any) {
    this.finalImage = texts.store_logo_url;

    if (texts.address) {
      this.rangeValue = texts.address.distance;
      this.address = texts.address.street + ', ' + texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state;
      this.geocodeAddress(texts.address.street + ', ' + texts.address.number + ', ' + texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state);
      this.complemento = texts.address.complement;
      this.about = texts.about;
      this.region = '';
      this.numero = texts.address.number;

      this.cidade = texts.address.city;
      this.bairro = texts.address.neighborhood;
      this.logradouro = texts.address.street;
      this.cep = texts.address.zipcode;
    }
    this.size = texts.size;
    this.sellerName = texts.seller_name
    this.documentCpf = texts.document;
    this.content = texts;
    this.name = texts.name;
    this.celular = texts.mobile;
    this.email = texts.email;
    this.publishAvailable = texts.seller_disable;
    this.publishAvailableText = this.publishAvailable ? 'Reativar conta' : 'Desativar conta';
    this.publishAvailableColor = this.publishAvailable ? 'editBox__formData__dataBtn--button__active' : 'editBox__formData__dataBtn--button__disable';
    this.catSubcat = texts.third_level;
    if (texts.sub_category) {
      // this.categorySubcategory = this.catSubcat.split(' - ');
      this.category = texts.category;
      this.subcategory = texts.sub_category;
      this.validSubCategory = this.subcategory ? true : false;
    }
    this.hideAddress = (texts.hide_address === true) ? texts.hide_address : false;

    this.valid = false;
    this.validAddress = this.address ? true : false;
    this.validCategory = this.category ? true : false;
    this.validComplemento = this.complemento ? true : false;
    this.validDescricao = this.descricao ? true : false;
    this.validNumero = this.numero ? true : false;
    this.validAbout = this.about ? true : false;
    this.validRegion = false;
    this.validLogradouro = false;
    this.validCidade = false;
    this.validBairro = false;
    this.validCep = false;

    this.focused.editCategory = this.category ? true : false;
    this.focused.editSize = this.size ? true : false;
    this.focused.editSellerName = this.sellerName ? true : false;
    this.focused.editAddress = this.address ? true : false;
    this.focused.editRegion = true;
    this.focused.editAbout = this.about ? true : false;
    this.focused.editComplemento = this.complemento ? true : false;
    this.focused.editDescricao = this.descricao ? true : false;
    this.focused.editNumero = this.numero ? true : false;
    this.focused.editCep = this.cep ? true : false;
    this.focused.editLogradouro = this.logradouro ? true : false;
    this.focused.editCidade = this.cidade ? true : false;
    this.focused.editBairro = this.bairro ? true : false;
    this.subcategoriesData = this.subcategories;
    this.clearSubcategory();
  }

  alterarAddress() {
    this.$emit('loading', true);

    const location = {
      distance: this.rangeValue ? this.rangeValue : null,
      number: this.numero,
      complement: this.complemento,
      neighborhood: this.bairro,
      lat: this.lat,
      lng: this.lng,
      city: this.cidade,
      street: this.logradouro,
      zipcode: this.cep,
      show_address: this.showAddress,
    };


    if (
      !location.number ||
      !location.zipcode ||
      !location.neighborhood ||
      !location.city ||
      !location.street
    ) {
      let emptyFields = "";

      if (!location.zipcode) { emptyFields += '\n Cep' }
      if (!location.street) { emptyFields += '\n Logradouro' }
      if (!location.number) { emptyFields += '\n Nmero' }
      if (!location.neighborhood) { emptyFields += '\n Bairro' }
      if (!location.city) { emptyFields += '\n Cidade' }

      this.toastMessage('error', "Os campos abaixo so obrigatrios:\n" + emptyFields);

      this.$emit('loading', false);

      return;
    }

    if (location) {
      this.userService.updateShowAddress(location)
      this.userService.updateSellerAddress(location)
        .then(data => {
          if (!data.ok) {
            this.showErrorNotification();
            this.$emit('loading', false);
          } else {
            this.userService.updateSeller({ hide_address: this.hideAddress })
              .then(userData => {
                if (!userData.ok) {
                  this.showErrorNotification();
                  this.$emit('loading', false);
                } else {
                  this.showSuccessfullNotification();
                  this.$emit('loading', false);
                  window.location.reload();
                }
              });
          }
        });

    }

    this.focused.editNumero = this.numero ? true : false;

    this.$emit('loading', false);
  }

  getSigla() {
    const obj = this.sizes.find((item) => item.value === this.size)
    return (obj !== undefined) ? obj.sigla : "";
  }

  saveData() {
    this.$emit('loading', true);
    if (!this.size || !this.sellerName) {
      alert("Deve preencher os dados em branco, por favor preencha e tente novamente");
      this.showErrorNotification();
      this.$emit('loading', false);
    } else {
      this.userService.updateSize(this.getSigla(), this.sellerName, this.name, this.email, this.documentCpf).then(data => {
        if (!data.ok) {
          data.json().then(obj => {
            if (obj.email) {
              this.showErrorNotification(obj.email[0]);
            } else {
              this.showErrorNotification();
            }
          })
        } else {
          this.showSuccessfullNotification();
          data.json().then(obj => {
            localStorage.setItem('loggedUser', JSON.stringify(obj));
            this.loggedUser = obj;
            window.location.reload();
          });
        }
      })
        .catch((error) => {
          console.log(error)
        })
        .finally(() => this.$emit('loading', false));
    }
  }

  alterSellerStatus() {
    if (this.publishAvailable) {
      this.userService.habilitaVendedor().then(value => {
        if (value.status !== 202) {
          this.showErrorNotification();
        } else {
          this.showSuccessfullNotification();
          this.publishAvailable = false;
          this.publishAvailableText = 'Desativar conta';
        }
      });
    } else {
      this.userService.desabilitaVendedor().then(value => {
        if (value.status !== 202) {
          this.showErrorNotification();
        } else {
          this.showSuccessfullNotification();
          this.publishAvailable = true;
          this.publishAvailableText = 'Reativar conta';
        }
      });
    }
    this.publishAvailableColor = this.publishAvailable ? 'editBox__formData__dataBtn--button__disable' : 'editBox__formData__dataBtn--button__active';
  }

  saveCategory() {
    this.$emit('loading', true);
    if (this.subcategory === '' && this.category !== '') {
      this.showErrorNotification();
      this.$emit('loading', false);
    } else {
      // const value = this.category + ' - ' + this.subcategory;
      // this.categoryId = this.categories.filter(c => c.value === value)[0].id;
      const subcategory = this.subcategories.find(subcategory => subcategory.name === this.subcategory);
      if (subcategory) {
        this.userService.updateCategory({ cnae: subcategory.cnae })
          .then(data => {
            if (!data.ok) {
              this.showErrorNotification();
              this.$emit('loading', false);
            } else {
              this.userService.updateSeller({ about: this.about })
                .then(userData => {
                  if (!userData.ok) {
                    this.showErrorNotification();
                  } else {
                    this.showSuccessfullNotification();
                    const logged: any = JSON.parse(localStorage.getItem('loggedUser') || '');
                    logged.category = this.category;
                    logged.subscribe = this.subcategory;
                    logged.about = this.about;
                    localStorage.setItem('loggedUser', JSON.stringify(logged));
                  }
                  this.$emit('loading', false);
                  window.location.reload();
                });
            }
          });
      } else {
        this.userService.updateSeller({ about: this.about })
          .then(data => {
            if (!data.ok) {
              this.showErrorNotification();
              this.$emit('loading', false);
            } else {
              this.showSuccessfullNotification();
              this.$emit('loading', false);
            }
          });
      }
    }
  }

  alterarSocialMedia() {
    window.event?.preventDefault();
    this.socialMedia = [];
    this.$emit('loading', true);
    this.instagramUser = false;
    this.facebookUser = false;
    localStorage.setItem('facebook', 'true');
    localStorage.setItem('instagram', 'true');
    if (!this.face) {
      this.face = '';
    }
    if (!this.instg) {
      this.instg = '';
    }

    if (this.facebook.length > 0) {
      this.verificar('facebook');
      setTimeout(() => {
        if (localStorage.getItem('facebook') === 'true') {
          this.socialMedia.push({
            status: this.facebook ? true : false,
            profileUrl: 'https://www.facebook.com/' + this.facebook,
            type: 'Facebook',
          });
        } else {
          this.socialMedia.push({
            status: this.face ? true : false,
            profileUrl: 'https://www.facebook.com/' + this.face,
            type: 'Facebook',
          });
        }
      }, 510);
    }
    if (this.instagram.length > 0) {
      setTimeout(() => {
        if (localStorage.getItem('instagram') === 'true') {
          this.socialMedia.push({
            status: this.instagram ? true : false,
            profileUrl: 'https://www.instagram.com/' + this.instagram,
            type: 'Instagram',
          });
        } else {
          this.socialMedia.push({
            status: this.instg ? true : false,
            profileUrl: 'https://www.instagram.com/' + this.instg,
            type: 'Instagram',
          });
        }
      }, 510);
      this.verificar('instagram');
    }
    if (this.website.length > 0) {
      this.socialMedia.push({
        status: this.website ? true : false,
        profileUrl: 'https://' + this.website,
        type: 'Personal Website',
      });
    }
    setTimeout(() => {
      this.userService.postSocialMedia(this.socialMedia)
        .then(data => {
          if (!data.ok || this.instagramUser !== false || this.facebookUser !== false) {
            this.showErrorNotification();
            this.$emit('loading', false);
            if (this.instagramUser === false) {
              this.instg = this.instagram;
            }
            if (this.facebookUser === false) {
              this.face = this.facebook;
            }
          } else {
            this.showSuccessfullNotification();
            this.$emit('loading', false);
            this.socialMedia = [];
            this.face = this.facebook;
            this.instg = this.instagram;
          }
        })
        .catch(err => console.log(err));
      localStorage.removeItem('instagram');
      localStorage.removeItem('facebook');
    }, 600);
  }


  // updatePostSocial() {
  //   this.$emit('loading', false);
  //   this.service.getSocialMedia()
  //   .then(data => data.json())
  //   .then(data => {
  //     if (!data.ok) {
  //       this.showErrorNotification();
  //       this.$emit('loading', false);
  //     } else {
  //       this.$emit('loading', false);
  //       this.socialMedia.push(data);
  //     }
  //   })
  //   .catch(err => {
  //     console.log(err);
  //   })
  // }

  /**
   * Param alterarPayments
   * @param  alterarPayments
   * funcao que seleciona o meio de pagamento,
   * altera o meio de pagamento
   * e envia o meio de pagamento
   */
  async alterarPayments() {
    window.event?.preventDefault();
    this.$emit('loading', true);
    await this.payments.forEach(paymentCategory => {
      paymentCategory.methods.forEach(method => {
        if (method.active) {
          this.checkedNames.push(method.name);
        }
      });
    });
    this.userService.listUpdatePagamentos(this.checkedNames)
      .then(paymentsChecked => {
        if (!paymentsChecked.ok) {
          this.showErrorNotification();
          this.$emit('loading', false);
        } else {
          this.$emit('loading', false);
          this.checked = true;
          this.checkedNames = [];
          this.showSuccessfullNotification();
        }
      });
  }

  cpfValidate() {
    if (this.documentCpf && this.documentCpf !== null && this.documentCpf.length > 0 && this.documentCpf.length <= 11) {
      this.documentCpf = this.documentCpf.replace(/(\d{3})(\d{3})(\d{3})(\d{2})/, (regex, arg1, arg2, arg3, arg4) => `${arg1}.${arg2}.${arg3}-${arg4}`);
    } else if (this.documentCpf !== null) {
      this.documentCpf = this.documentCpf.replace(/(\d{2})(\d{3})(\d{3})(\d{4})(\d{2})/, (regex, arg1, arg2, arg3, arg4, arg5) => `${arg1}.${arg2}.${arg3}/${arg4}-${arg5}`);
    }
  }

  celularValidate() {
    this.celular = this.celular.replace(/(\d{2})(\d{5})(\d{4})/, (regex, arg1, arg2, arg3) => `(${arg1}) ${arg2} - ${arg3}`);
  }

  public isCategoryValid() {
    this.validCategory = (this.category) ? (this.reg.test(this.category)) ? true : false : false;
  }

  public isAddressValid() {
    this.validAddress = (this.address === '')
      ? (this.reg.test(this.address))
        ? true
        : false
      : false;
  }

  public isRegionValid() {
    this.validRegion = (this.region === '') ? (this.reg.test(this.region)) ? true : false : false;
  }

  public isCepValid() {
    this.validCep = (this.cep === '') ? (this.reg.test(this.cep)) ? true : false : false;
  }

  public isBairroValid() {
    this.validBairro = (this.bairro === '') ? (this.reg.test(this.bairro)) ? true : false : false;
  }

  public isCidadeValid() {
    this.validCidade = (this.cidade === '') ? (this.reg.test(this.cidade)) ? true : false : false;
  }

  public isLogradouroValid() {
    this.validLogradouro = (this.logradouro === '') ? (this.reg.test(this.logradouro)) ? true : false : false;
  }

  async mounted() {
    this.autocompleteInit();
    this.initMap();
    // this.combo();
    this.getInstagramCode();
    const sellerObject = await this.userService.getSellerShowAddress()
    this.showAddressChecked = sellerObject.show_address;
    this.showAddress = sellerObject.show_address;
  }


  async infoAlertNoDocument() {
    this.notify = {
      icon: require('./../../../assets/images/icon_error.png'),
      text: `
          <strong>ATENO: Sua loja no est sendo exibida!</strong><br />Para voc ficar visvel aos seus clientes,  necessrio que preencha um nmero de documento vlido do seu negcio ou do responsvel legal.
        `,
      color: '#ffd1c4',
      active: true,
      type: 'error',
      text_align_left: true,
    };
    const response = await this.registerService.validateTokenSeller();
    if (!response.ok) {
      localStorage.removeItem('pertinho');
      localStorage.removeItem('loggedUser');
      this.$router.push('login');
    }
  }

  async infoAlertNoCategory() {
    this.notify = {
