  plotCircle(lat, lng) {
    this.removeAllcircles();
    const circulo = new google.maps.Circle({
      map: this.map,
      center: new google.maps.LatLng(lat, lng),
      radius: this.rangeValue * 1000,
      strokeColor: '#00b0d0',
      strokeOpacity: 0.8,
      strokeWeight: 1,
      fillColor: '#00b0d0',
      fillOpacity: 0.35,
    });
    this.circles.push(circulo);
    const bounds = new google.maps.LatLngBounds();
    bounds.union(circulo.getBounds());
    this.map.fitBounds(bounds, 1);
    this.map.setCenter({ lat, lng });
  }

