  async updateLogo() {
    this.$emit('loading', true);
    const compressor = new Compressor(this.imageToCrop, {
      quality: 0.5,
      width: 1250,
      height: 1250,
      convertSize: 0,
      success: (imageCompressed) => {
        const reader = new FileReader();
        if (imageCompressed.size / 1000000 < 6) {
          this.imageToCrop = imageCompressed;
          reader.readAsDataURL(imageCompressed);
          reader.onload = (e: any) => {
            this.imageToCrop = e.target.result;
            this.$emit('loading', false);
            this.destination = e.target.result;
            this.cropInit();
          };
        } else {
          alert("Reduza o tamanho de imagem e tente novamente.");
          this.$emit('loading', false);
        }
      },
