  }

  combo() {
    const teste = (this.categories.length) - 1;
    let i = 0;
    const cat: any[] = [];
    const testecat: any[] = [];
    let c;
    let scat: any[] = [];
    for (c = 0; c <= teste; c++) {
      cat[c] = this.categories[c].value.split(' - ');
    }
    for (c = 0; c < teste; c++) {
      testecat[c] = cat[c][0];
    }
    this.comboCategories[i] = testecat[0];
    for (c = 0; c <= teste; c++) {
      if (this.comboCategories[i] !== testecat[c]) {
        this.comboCategories.push(testecat[c]);
        i = i + 1;
      }
    }
    this.comboCategories.pop();
    const teste2 = this.comboCategories.length - 1;

    for (i = 0; i <= teste2; i++) {
      scat = [];
      let j = 0;
      for (c = 0; c <= teste; c++) {
        if (cat[c][0] === this.comboCategories[i]) {
          scat[j] = cat[c][1];
          j++;
        }
        this.subcategories[i] = scat;
        this.subcategoriesData[i] = scat;
      }
    }
    this.filterCategory();
    this.filterSubCategory();
  }

  filterSubCategories() {
    const re = new RegExp(this.subcategory, 'gi');
    let j = 0;
    let subcat: any[] = [];

    if (this.category !== null) {
      for (j; j < this.comboCategories.length; j++) {
        if (this.category === this.comboCategories[j]) {
          subcat = [];
          subcat = this.subcategories[j];
        }
      }
    }
    return subcat.filter(c => c.toString().toLowerCase().search(re) > -1);
  }

