import { Component, Prop, Vue } from 'vue-property-decorator';
import { Carousel, Slide } from 'vue-carousel';

import INotification from '@/models/INotification';
import OrderStatistics from '@/models/orderStatistics';
import OrderService from '@/services/orders.service';
import userService from '@/services/user.service';
import productsService from '@/services/products.service';
import categoryService from '@/services/category.service';
import shippingService from '@/services/shipping.service';

import getUtm from '@/helpers/getUtm';

declare global {
  interface Window {
    opera: any;
  }
}

@Component({
  components: {
    Carousel,
    Slide,
  },
})

export class meuNegocio extends Vue {

  @Prop({ default: '<strong>Minha loja online!</strong>' }) public titulo!: string;

  public pageSelected: string = 'loja';
  public qntprod: any = '';
  public moreProduct: boolean = false;
  public produtos: any[] = [];
  public allCategories: any[] = [];
  public allProducts: any[] = [];
  public shipping: any = '';

  public modalLinkCompartilhe: boolean = false;
  public modalCompartilhe: boolean = false;
  public lex: boolean = false;
  public slugUrl?: string;
  public notify?: INotification | string = ``;
  public content: any = '';
  public newPrice = {
    money: {
      decimal: ',',
      thousands: '.',
      prefix: 'R$ ',
      precision: 2,
      masked: true,
    },
    value: '',
    error: false,
    errorMsg: '',
  };

  public oldPrice = {
    money: {
      decimal: ',',
      thousands: '.',
      prefix: 'R$ ',
      precision: 2,
      masked: true,
    },
    value: '',
    error: false,
    errorMsg: '',
  };

  public address: any;

  public checkedNames: any[] = [];
  public checked: boolean = false;
  public items: object[] = [];
  public categoryImg: any;

  public hasPaymentMethod: boolean = false;
  public hasProducts: boolean = false;
  public hasShipping: boolean = false;

  public loggedUser: any;

  public orderStatistics: OrderStatistics = {};

  private userService = new userService();
  private orderService = new OrderService();
  private productService = new productsService();
  private categoryService = new categoryService();
  private shippingService = new shippingService();

  get isApp() {
    return this.$store.state.environment.isApp;
  }

  get menuItems () {
    if (this.isApp) {
      return [
        {
          label: 'Loja',
          value: 'loja',
          icon: 'store'
        },
        {
          label: 'Pedidos',
          value: 'pedidos',
          icon: 'receipt'
        },
        {
          label: 'Produtos',
          value: 'produtos',
          icon: 'shopping_bag'
        },
      ]
    } else {
      return [
        {
          label: 'Vitrine',
          value: 'loja',
          icon: 'store'
        },
        {
          label: 'Produtos/Pedidos',
          value: 'pedidos',
          icon: 'receipt'
        },
      ]
    }
  }

  constructor() {
    super();
    this.modalLinkCompartilhe = false;
    this.modalCompartilhe = false;
    this.userService.listProdcutsAll().then(data => {
      localStorage.setItem('loggedUser', JSON.stringify(data));
      this.setField(data);
      this.frete();
      if (!data.document) {
        this.infoAlertNoDocument(data);
      } else if (!data.category || !data.about) {
        this.infoAlertNoCategory(data);
      } else if (!data.address) {
        this.infoAlertNoAdress(data);
      } else if (!data.mobile_confirmed) {
        this.lex = !this.lex;
        this.notificar(data);
      } else if (!data.has_payment_method) {
        this.infoAlertPayments(data);
      } else if (!data.has_shipping) {
        this.infoAlertFrete(data);
      } else if (!data.has_products) {
        this.infoAlertProducts(data);
      }
    });
  }

  async created() {
    await this.getOrderView();
  }

  async getOrderView() {
    await this.orderService.getOrderView()
      .then((orderStatistics: OrderStatistics) => this.orderStatistics = orderStatistics)
      .catch(error => console.error(error));
  }

  frete() {
    const logged: any = localStorage.getItem('loggedUser');
    this.loggedUser = JSON.parse(logged);
    this.shippingService.getShippingDetail(this.loggedUser.slug)
      .then(data => {
        if (data.ok) {
          data.json().then(shipping => {
            this.shipping = shipping;
            if (shipping.estimated_time === '01:00:00') {
              this.shipping.estimated_time = 'Em até 1h';
            }
            if (shipping.estimated_time === '23:59:59') {
              this.shipping.estimated_time = 'Em até 1d';
            }
            if (shipping.estimated_time === '00:00:00') {
              this.shipping.estimated_time = 'a combinar';
            }
          });
        }
      });
  }

  goToProducts() {
    this.$router.push('produtos');
  }

  setField(texts: any) {
    (texts.store_logo_url)
      ? this.categoryImg = texts.store_logo_url
      : this.categoryImg = texts.category_image_url;

    this.content = texts;
    this.slugUrl = `https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}`;
    this.content.about = texts.about;

    if (texts.address) {
      (texts.hide_address)
        ? this.content.address = texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state
        : this.content.address = texts.address.street + ', ' + texts.address.number + ', ' + texts.address.neighborhood + ', ' + texts.address.city + ', ' + texts.address.state;
    }

    this.content.mobile = this.content.mobile.replace(/(\d{2})(\d{5})(\d{4})/, (regex, arg1, arg2, arg3) => `(${arg1}) ${arg2} - ${arg3}`);
    setTimeout(() => {
      this.getAllCategoriesProducts();
      this.isMobile();
    }, 30);
  }

  notificar(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
          <strong>ATENÇÃO: Sua loja não está sendo exibida!</strong><br>
          ${
            data.mobile_confirmed
          ? ''
          : 'Você não ativou a sua conta via WhatsApp. Por favor, verifique se você não recebeu o link de ativação no seu WhatsApp. Se não recebeu, por favor clique no botão abaixo para reenviar a mensagem'
        }`,
      color: '#ffd1c4f0',
      buttons: true,
      buttonText: 'REENVIAR MENSAGEM',
      activeButtons: true,
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  infoAlertBlog(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
          <strong>A Pertinho agora tem mais uma novidade!</strong>
          <br/>
          Que tal trocarmos experiências com outros empreendedores na comunidade de empreendedores do Facebook?
          <br/>
          Faça parte agora mesmo <a class="text-dark" style="text-decoration: underline;" href="https://www.facebook.com/groups/1020347875087965/" target="_blank">clicando aqui.</a>
        `,
      color: '$primary',
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  infoAlertNoDocument(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
          <strong>ATENÇÃO: Sua loja não está sendo exibida!</strong>
          <br/>
          Para você ficar visível aos seus clientes, é necessário que preencha um número de documento válido do seu negócio ou do responsável legal.
        `,
      color: '#ffd1c4',
      active: true,
      type: 'error',
      text_align_left: true,
      buttons: true,
      url: 'edit#document',
      buttonText: 'PREENCHER AGORA',
      activeButtons: true,
    });
  }

  infoAlertNoCategory(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
          <strong>ATENÇÃO: Sua loja não está sendo exibida!</strong>
          <br/>
          Para você ficar visível aos seus clientes, é necessário que preencha uma categoria, uma subcategoria e uma descrição para o seu negócio.
        `,
      color: '#ffd1c4',
      active: true,
      type: 'error',
      url: 'edit#category',
      text_align_left: true,
      buttons: true,
      buttonText: 'PREENCHER AGORA',
      activeButtons: true,
    });
  }

  infoAlertNoAdress(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
          <strong>ATENÇÃO: Sua loja não está sendo exibida!</strong>
          <br/>Para você ficar visível aos seus clientes, é necessário que preencha o endereço do seu negócio e o raio de atendimento.
        `,
      color: '#ffd1c4',
      active: true,
      type: 'error',
      url: 'edit#address',
      text_align_left: true,
      buttons: true,
      buttonText: 'PREENCHER AGORA',
      activeButtons: true,
    });
  }

  infoAlertAvailableConta(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
        Falta pouco.....
        `,
      color: '$primary',
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  infoAlertPayments(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><br>Mostre aos seus clientes quais meios de pagamento você aceita.<br >Cadastre agora mesmo <strong><a href="edit#meiosdepagamentos" style="color:#525252">clicando aqui</a></strong>.
        `,
      color: '$primary',
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  infoAlertFrete(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><br>Mostre aos seus clientes quais são suas condições de frete. <Br>Cadastre agora mesmo <strong><a href="edit#frete" style="color:#525252">clicando aqui</a></strong>.
        `,
      color: '$primary',
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  infoAlertProducts(data) {
    this.$emit('notification', {
      icon: require('./../../assets/images/icon_error.png'),
      text: `
        <strong>Falta pouco para ter sua lojinha publicada!</strong><Br>Cadastre seus produtos e comece a vender através da sua lojinha. <br >Cadastre agora mesmo <strong><a href="produtos" style="color:#525252">clicando aqui</a></strong>.
        `,
      color: '#c3e4e7',
      active: true,
      type: 'error',
      text_align_left: true,
    });
  }

  returnUserAgent() {
    let check = false;
    ((a) => {
      if (/(android|bb\d+|meego).+mobile|avantgo|bada\/|blackberry|blazer|compal|elaine|fennec|hiptop|iemobile|ip(hone|od)|iris|kindle|lge |maemo|midp|mmp|mobile.+firefox|netfront|opera m(ob|in)i|palm( os)?|phone|p(ixi|re)\/|plucker|pocket|psp|series(4|6)0|symbian|treo|up\.(browser|link)|vodafone|wap|windows ce|xda|xiino/i.test(a) ||
        /1207|6310|6590|3gso|4thp|50[1-6]i|770s|802s|a wa|abac|ac(er|oo|s\-)|ai(ko|rn)|al(av|ca|co)|amoi|an(ex|ny|yw)|aptu|ar(ch|go)|as(te|us)|attw|au(di|\-m|r |s )|avan|be(ck|ll|nq)|bi(lb|rd)|bl(ac|az)|br(e|v)w|bumb|bw\-(n|u)|c55\/|capi|ccwa|cdm\-|cell|chtm|cldc|cmd\-|co(mp|nd)|craw|da(it|ll|ng)|dbte|dc\-s|devi|dica|dmob|do(c|p)o|ds(12|\-d)|el(49|ai)|em(l2|ul)|er(ic|k0)|esl8|ez([4-7]0|os|wa|ze)|fetc|fly(\-|_)|g1 u|g560|gene|gf\-5|g\-mo|go(\.w|od)|gr(ad|un)|haie|hcit|hd\-(m|p|t)|hei\-|hi(pt|ta)|hp( i|ip)|hs\-c|ht(c(\-| |_|a|g|p|s|t)|tp)|hu(aw|tc)|i\-(20|go|ma)|i230|iac( |\-|\/)|ibro|idea|ig01|ikom|im1k|inno|ipaq|iris|ja(t|v)a|jbro|jemu|jigs|kddi|keji|kgt( |\/)|klon|kpt |kwc\-|kyo(c|k)|le(no|xi)|lg( g|\/(k|l|u)|50|54|\-[a-w])|libw|lynx|m1\-w|m3ga|m50\/|ma(te|ui|xo)|mc(01|21|ca)|m\-cr|me(rc|ri)|mi(o8|oa|ts)|mmef|mo(01|02|bi|de|do|t(\-| |o|v)|zz)|mt(50|p1|v )|mwbp|mywa|n10[0-2]|n20[2-3]|n30(0|2)|n50(0|2|5)|n7(0(0|1)|10)|ne((c|m)\-|on|tf|wf|wg|wt)|nok(6|i)|nzph|o2im|op(ti|wv)|oran|owg1|p800|pan(a|d|t)|pdxg|pg(13|\-([1-8]|c))|phil|pire|pl(ay|uc)|pn\-2|po(ck|rt|se)|prox|psio|pt\-g|qa\-a|qc(07|12|21|32|60|\-[2-7]|i\-)|qtek|r380|r600|raks|rim9|ro(ve|zo)|s55\/|sa(ge|ma|mm|ms|ny|va)|sc(01|h\-|oo|p\-)|sdk\/|se(c(\-|0|1)|47|mc|nd|ri)|sgh\-|shar|sie(\-|m)|sk\-0|sl(45|id)|sm(al|ar|b3|it|t5)|so(ft|ny)|sp(01|h\-|v\-|v )|sy(01|mb)|t2(18|50)|t6(00|10|18)|ta(gt|lk)|tcl\-|tdg\-|tel(i|m)|tim\-|t\-mo|to(pl|sh)|ts(70|m\-|m3|m5)|tx\-9|up(\.b|g1|si)|utst|v400|v750|veri|vi(rg|te)|vk(40|5[0-3]|\-v)|vm40|voda|vulc|vx(52|53|60|61|70|80|81|83|85|98)|w3c(\-| )|webc|whit|wi(g |nc|nw)|wmlb|wonu|x700|yas\-|your|zeto|zte\-/i.test(a.substr(0, 4))) {
        check = true;
      }
    })(navigator.userAgent || navigator.vendor || window.opera);
    return check;
  }

  compLink() {
    (document.getElementById('myText') as HTMLInputElement).value = `https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('link', false)}`;
  }

  copiarLink() {
    const copyText = document.getElementById('myText');
    (copyText as HTMLInputElement).select();
    (copyText as HTMLInputElement).setSelectionRange(0, 99999);
    document.execCommand('copy');
    alert('Link copiado!');
  }

  complinkLoja() {
    window.open(`https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('link', false)}`);
  }

  compWhatsapp() {
    window.open(`https://api.whatsapp.com/send?text=Ei,%20pessoal!%20Conheçam%20a%20minha%20loja%20dentro%20da%20Pertinho%20de%20Casa!%20Compre%20no%20site%20ou%20pelo%20app!%0Ahttps://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('whatsapp')}`);
  }

  compFacebook() {
    window.open(`https://www.facebook.com/sharer/sharer.php?u=http%3A%2F%2Fwww.pertinhodecasa.com.br%2Fvitrine%2F${this.content.slug}?${getUtm('facebook')}&src=sdkpreparse`);
  }

  compMessenger() {
    window.open(`https://www.facebook.com/login.php?u=http%3A%2F%2Fwww.pertinhodecasa.com.br%2Fvitrine%2F${this.content.slug}?${getUtm('messenger')}&src=sdkpreparse`);
  }

  compTwitter() {
    window.open(`https://twitter.com/intent/tweet?url=https://www.pertinhodecasa.com.br/vitrine/${this.content.slug}?${getUtm('twitter')}&text=Ei,%20pessoal!%20Conheçam%20a%20minha%20loja%20dentro%20da%20Pertinho%20de%20Casa!%20Compre%20no%20site%20ou%20pelo%20app!`);
  }

  validateCurrency(x) {
    let money;
    if (x) {
      money = x.replace('R$ ', '');
      money = money.replace(',', '');
      money = money.replace('.', '');
    }
    return money;
  }

  getAllCategoriesProducts() {
    this.qntprod = 0;
    const logged: any = localStorage.getItem('loggedUser');
    this.loggedUser = JSON.parse(logged);
    this.categoryService.getCategories(this.loggedUser.slug)
      .then(data => {
        if (data.ok) {
          data.json().then(async response => {
            await response.forEach(category => {
              this.productService.getProducts(category.guid)
                .then(productData => {
                  if (productData.ok) {
                    productData.json().then(async (products: any) => {
                      await products.map(x => {
                        if (x.skus) {
                          if (parseFloat(this.validateCurrency(x.skus.old_price)) > parseFloat(this.validateCurrency(x.skus.new_price))) {
                            Vue.set(x.skus, 'sale', true);
                            x.skus.sale = true;
                            this.allProducts.push(x);
                          } else {
                            Vue.set(x.skus, 'sale', false);
                            x.skus.sale = false;
                            this.allProducts.push(x);
                          }
                        }
                        this.qntprod = this.qntprod + 1;
                      });
                      const produtos: any[] = [];
                      produtos.push(products[0]);

                      (produtos[0])
                        ? Vue.set(category, 'products', products)
                        : Vue.set(category, 'products', false);
                    });
                  }
                });
            });
            this.allCategories = response;
          });
        }
      });
  }

  isMobile() {
    if (this.returnUserAgent()) {
      const element: HTMLElement = document.getElementById('negocio') as HTMLElement;
      const element2: HTMLElement = document.getElementById('produtos') as HTMLElement;
      if (element && element2) {
        element.setAttribute('style', 'left: 0%;transition: 0.5s ease-in-out;');
        element2.setAttribute('style', 'left: 0%;transition: 0.5s ease-in-out;');

        setTimeout(() => {
          element.setAttribute('style', 'left: -100%;transition: 0.5s ease-in-out;');
          element2.setAttribute('style', 'left: -100%;transition: 0.5s ease-in-out;');
        }, 10);

        document.addEventListener('touchmove', () => {
          element.setAttribute('style', 'left: 0%;transition: 0.5s ease-in-out;');
          element2.setAttribute('style', 'left: 0%;transition: 0.5s ease-in-out;');
        });
      }
    }
  }


  onPageSelected(selected: string) {
    setTimeout(scope => scope.pageSelected = selected, 500, this);
  }
}
